package com.example.demo.modules.article.service.impl;import com.example.demo.domain.TeArticle;import com.example.demo.domain.TeCategory;import com.example.demo.mapper.TeArticleMapper;import com.example.demo.mapper.TeCategoryMapper;import com.example.demo.modules.article.pojo.bo.ArticleBO;import com.example.demo.util.RandomString;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.ArrayList;import java.util.Date;import java.util.List;/** * @description: * @author: Mr.monster.liu * @create: 2021-05-31 14:54 **/@Service@Slf4jpublic class ArticleServiceImpl {    @Resource    private TeCategoryMapper teCategoryMapper;    @Resource    private TeArticleMapper teArticleMapper;    public TeCategory insertCategory(){        String randomStr = RandomString.getRandomString(32);        String remarkStr = RandomString.getRandomString(64);        TeCategory teCategory = new TeCategory();        teCategory.setName(randomStr);        teCategory.setRemark(remarkStr);        teCategoryMapper.insertCategory(teCategory);        return  teCategory;    }}