package com.example.demo.modules.async.service;import com.example.demo.modules.design.builder.MustCalculationBuilder;import com.example.demo.modules.design.builder.service.CalculationService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Service;/** * @description: 多线程 * @author: Mr.monster.liu * @create: 2021-07-12 14:52 **/@Servicepublic class AsyncTaskService {    @Autowired    private CalculationService calculationService;    @Async    public void executeAsyncTask(int i) {        MustCalculationBuilder index = calculationService.index(i);        System.out.println("线程" + Thread.currentThread().getName()+" 执行异步任务：" + i+"结果===="+index.toString());    }}