package com.example.demo.modules.designPattern.sevice.builder;/** * @description: 汽车零件实现类 * @author: Mr.monster.liu * @create: 2021-06-17 11:45 **/public class Manufacturer implements ManufacturerInterface {    private Cart cart;    public Manufacturer(){        this.cart= new Cart();    }    @Override    public  Manufacturer createTyre(String tyre) {        this.cart.setTyre(tyre);        return this;    }    @Override    public Manufacturer createEngine(String engine) {        this.cart.setEngine(engine);        return this;    }    @Override    public Manufacturer createFuselage(String fuselage) {        this.cart.setFuselage(fuselage);        return this;    }    @Override    public Manufacturer getCart() {        return this;    }}