package com.example.demo.modules.entrust.service.impl;import com.example.demo.modules.entrust.service.EntrustInterface;import org.springframework.stereotype.Service;/** * @description: 计算价格服务类 * @author: Mr.monster.liu * @create: 2021-06-28 10:37 **/@Servicepublic class CalculateThePriceService {    /**     *  商品根据用户等级 计算对应的会员价格     *     *  商品价格区别去 活动（砍价 拼团 秒杀 ） 商品分类等     *     **/    private EntrustInterface entrustInterface;    /**     * 设置会员的级别或者等级信息     * @param entrustInterface     */    public void calculateThePriceService(EntrustInterface entrustInterface){        this.entrustInterface = entrustInterface;    }    public CalculateThePriceService calculateThePrice(Double price){        this.entrustInterface.calculateThePrice();        return  this;    }}