package com.example.demo.modules.entrust.service.impl;import com.example.demo.domain.TeGoods;import com.example.demo.domain.TeUsers;import com.example.demo.modules.entrust.service.EntrustInterface;import org.springframework.stereotype.Component;/** * @description: 高级会员价格计算实现类 * @author: Mr.monster.liu * @create: 2021-06-28 10:47 **/public class SeniorMemberEntrustServiceImpl implements EntrustInterface {    private TeUsers user;    private TeGoods goods;    @Override    public void setUser(TeUsers user) {        this.user = user;    }    @Override    public void setGoods(TeGoods goods) {        this.goods = goods;    }    @Override    public double calculateThePrice() {        System.out.println("高级会员价格计算实现类");        Integer price =  this.user.getIntegral() * this.goods.getStock();        return price;    }}