package com.example.demo.modules.rabbitmq.service;import cn.hutool.json.JSONObject;import cn.hutool.json.JSONUtil;import com.example.demo.rabbitmq.RabbitMqRpcConfig;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.io.IOException;/** * @description: 发送rpc业务类 * @author: Mr.monster.liu * @create: 2021-06-07 14:06 **/@Service@Slf4jpublic class SendRabbitRpcMsgService {    @Autowired    private RabbitMqRpcConfig rabbitMqRpcConfig;    public void index() throws IOException, InterruptedException {        Object o = new Object();        String s = JSONUtil.toJsonStr(o);        String call = rabbitMqRpcConfig.call("rpc.test", s.getBytes(), "1000", 1000);        log.info(call);    }}