package com.example.demo.rabbitmq;import lombok.Getter;/** * @description: 延迟队列enum * @author: Mr.monster.liu * @create: 2021-09-07 10:17 **/@Getterpublic enum  DelayQueueEnum {    /**     * 发送短信消息通知队列     */    QUEUE_MSG_SEND("delay.msg.direct", "普通消息队列", "delay.msg.send"),    /**     * 发送邮件消息通知队列     */    QUEUE_EMAIL_SEND("delay.email.direct", "发送email消息", "delay.email.send"),    /**     * 消息通知ttl队列     *     */    QUEUE_TTL_DIRECT_CANCEL("delay.direct.ttl", "死信消息队列", "delay.cancel.ttl");    /**     * 交换机名称     */    private String exchange;    /**     * 队列名称     */    private String name;    /**     * 路由键     */    private String routeKey;    DelayQueueEnum(String exchange, String name, String routeKey) {        this.exchange = exchange;        this.name = name;        this.routeKey = routeKey;    }}