package com.example.demo.rabbitmq.service;import com.example.demo.rabbitmq.RabbitMqEnum;import com.rabbitmq.client.Channel;import org.springframework.amqp.core.Message;import org.springframework.amqp.rabbit.annotation.RabbitListener;import org.springframework.stereotype.Component;import java.io.IOException;/** * @description: 交换机监听 * @author: Mr.monster.liu * @create: 2021-06-07 16:14 **/@Componentpublic class TopicReceiver {    @RabbitListener(queues = "topic.test1")    public void TestExchangeQueue(Message testMessage, Channel channel) throws IOException {        System.out.println("TestExchangeQueue消费者收到消息  : " + testMessage.toString());        channel.basicAck(testMessage.getMessageProperties().getDeliveryTag(), true);    }    @RabbitListener(queues = "topic.test2")    public void TestExchangeQueue2(Message testMessage,Channel channel) throws IOException {        System.out.println("TestExchangeQueue2消费者收到消息  : " + testMessage.toString());        channel.basicAck(testMessage.getMessageProperties().getDeliveryTag(), true);    }}