package com.example.demo.util;import java.util.Random;/** * @description: 随机字符串生成 * @author: Mr.monster.liu * @create: 2021-05-31 14:33 **/public  class RandomString {    public static String getRandomString(int length){        String str="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";        Random random=new Random();        StringBuffer sb=new StringBuffer();        for(int i=0;i<length;i++){            int number=random.nextInt(62);            sb.append(str.charAt(number));        }        return sb.toString();    }}